    //------------------------------------------------------------------------------------------------
    //     roll function
    static int[] roll(int n) {
        int max = 6;
        int min = 1;
        int range = max - min + 1;
        int[] array = new int[n];
        for (int i = 0; i < array.length; i++) {
            array[i] = (int) (Math.random() * range) + min;
        }

        return array;
    }

    //------------------------------------------------------------------------------------------------
    // contains Duplicates function
    static boolean containsDuplicates(int[] arr) {
        Set<Integer> s = new HashSet<Integer>();
        for (int i = 0; i < arr.length; i++) {
            s.add(arr[i]);
        }
        if (s.size() < 6) {
            return true;
        } else {
            return false;
        }
    }

    //------------------------------------------------------------------------------------------------
    // avg function
    static int arrayAvg(int[] arr) {
        int sum = 0;
        int avg;
        for (int ele : arr) {
            sum += ele;
        }

        if (arr.length > 0) {
            avg = sum / arr.length;
        } else {
            avg = 0;
        }
        return avg;
    }

    //------------------------------------------------------------------------------------------------
//    2DArray funcation
    static int[] multi(int[][] array) {
        int sum = 0, avg = 0;
        ArrayList<Integer> avgArr = new ArrayList<Integer>();
        for (int i = 0; i < array.length; i++) {
            for (int ele : array[i]) {
                sum += ele;
            }
            avg = sum / array[i].length;
            avgArr.add(avg);
            sum = 0;
        }
        int arrNum = avgArr.indexOf(Collections.min(avgArr));
        return array[arrNum];
    }

